        this.state={
            counter:0
        }
        this.handleClick=this.handleClick.bind(this)
    }
    handleClick(event){
        this.setState({counter:this.state.counter+1});
        console.log(this.state);
    }
    render(){
        var text =this.state.counter;
        return(
            <p onClick={this.handleClick}>
                你點了{text}次.
            </p>
        )
    }
}
