import React from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';
class UserGithub extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            userid:'',
            username: '',
            githubtUrl: '',
            avatarUrl: '',
            flower_amount:'',
        }
    }

    componentDidMount() {

        // $.get('this.props.source')
        // .then(result => {
        //     const data = result;
        //     if (this._isMounted) {
        //         this.setState({
        //             userid: data.id,
        //             username: data.login,
        //             githubtUrl: data.html_url,
        //             avatarUrl: data.avatar_url,
        //         });
        //     }
        // });

        $.get(this.props.source, (result) => {
            console.log(this.props.source);
            console.log(result);
            const data = result;
            if (data) {
                this.setState({
                    userid: data.id,
                    username: data.login,
                    githubtUrl: data.html_url,
                    avatarUrl: data.avatar_url,
                    flower_amount:data.followers,
                });
            }
        });
    }
    componentWillUnmount() {
        this.setState = ()=>false;
    }
    render() {
        return (
            <div>
                <img src={this.state.avatarUrl} />
                <h3>name:{this.state.username}</h3>
                <h6>{this.state.userid}</h6>
                <h3>folloers:{this.state.flower_amount}</h3>
                <a href={this.state.githubtUrl}>Github Link<br></br></a>.
                <a href={this.state.githubtUrl}>Github Link</a>.

            </div>
        );
    }
    
}

ReactDOM.render(
    <UserGithub source="https://api.github.com/users/STARZ333" />,
    document.getElementById('root')
);

export default UserGithub;
