import React from 'react';
import ReactDOM from 'react-dom';
import $ from 'jquery';
class UserGithub extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            githubtUrl: '',
            avatarUrl: '',
        }
    }

    componentDidMount() {

        // $.get('this.props.source')
        // .then(result => {
        //     const data = result;
        //     if (this._isMounted) {
        //         this.setState({
        //             userid: data.id,
        //             username: data.login,
        //             githubtUrl: data.html_url,
        //             avatarUrl: data.avatar_url,
        //         });
        //     }
        // });

        $.get(this.props.source, (result) => {
            console.log(result);
            const data = result;
            if (data) {
                this.setState({
                    userid: data.id,
                    username: data.login,
                    githubtUrl: data.html_url,
                    avatarUrl: data.avatar_url
                });
            }
        });
    }

    render() {
        return (
            <div>
                <img src={this.state.avatarUrl} />
                <h3>name:{this.state.username}</h3>
                <h6>{this.state.userid}</h6>
                <a href={this.state.githubtUrl}>Github Link</a>.
            </div>
        );
    }
    componentWillUnmount = () => {
        this.setState = (state,callback)=>{
          return;
        };
    }
}

ReactDOM.render(
    <UserGithub source="https://api.github.com/users/STARZ333" />,
    document.getElementById('root')
);

export default UserGithub;
